def format_build(components):
    result = "Ваша сборка:\n\n"
    for component in components:
        result += f"{component['name']}\nЦена: {component['price']}\nСсылка: {component['link']}\n\n"
    return result

def save_user_request(user_id, request):
    with open("user_requests.txt", "a") as f:
        f.write(f"{user_id}: {request}\n")